import './App.css';
import './data.js'
import { useState } from 'react';
import ShowData from './components/ShowData';

//-----------------------------------------------------Bài tập 1---------------------------------------------------------//


function App() {
  const [renderData, editRenderData] = useState(data)

  const handleCompletedClick = () => {
    const reData = data.filter((d) => {
      if(d.completed === true) {return d}
    })
    editRenderData(reData)
  }

  const handleNotCompletedClick = () => {
    const reData = data.filter((d) => {
      if(d.completed === false) {return d}
    })
    editRenderData(reData)
  }

  const handleAllClick = () => {
    editRenderData(data)
  }

  return (
    <div className='App'>
      <button onClick={handleCompletedClick}>completed</button>
      <button onClick={handleNotCompletedClick}>not completed</button>
      <button onClick={handleAllClick}>All</button>
      <ShowData data={renderData}/>
    </div>
  );
}

export default App;


