import './App.css';
import { useState } from 'react';


function App() {
  const [userInputValue, setUserInputValue] = useState('')
  const [firstInputValue, setFirstInputValue] = useState('')
  const [secondInputValue, setSecondInputValue] = useState('')
  const [text, setText] = useState('')

  const handleUserInputChange = (e) => {
    setUsnputValue(e.target.value)
  }

  const handleFirstInputChange = (e) => {
    setFirstInputValue(e.target.value)
  }

  const handleSecondInputChange = (e) => {
    setSecondInputValue(e.target.value)
  }

  const handleSubmit = (e) => {
    e.preventDefault()
    if(firstInputValue === secondInputValue) {
      setText('Input giống nhau')
    } else {
      setText('Bạn và tôi không giống nhau, con đường tôi bước đi, chỉ tôi mới hiểu được')
    }
    setFirstInputValue('')
    setSecondInputValue('')
  }

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <input type='text' value={firstInputValue} onChange={handleFirstInputChange}></input>
        <input type='text' value={firstInputValue} onChange={handleFirstInputChange}></input>
        <input type='text' value={secondInputValue} onChange={handleSecondInputChange}></input>
        <button type='submit'>Submit</button>
      </form>
      <p>{text}</p>
    </div>
  );
}

export default App;
