  const [text, setText] = useState('')

  const handleFirstInputChange = (e) => {
    setFirstInputValue(e.target.value)
  }

  const handleSecondInputChange = (e) => {
    setSecondInputValue(e.target.value)
  }

  const handleSubmit = (e) => {
    e.preventDefault()
    firstInputValue === secondInputValue ? setText('Quen roi') : null
    setFirstInputValue('')
    set
  }

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <input type='text' value={firstInputValue} onChange={handleFirstInputChange}></input>
        <input type='text' value={secondInputValue} onChange={handleSecondInputChange}></input>
        <button type='submit'>Submit</button>
      </form>
      <p>{text}</p>
    </div>
  );
}

export default App;
