import './App.css';
import { useState } from 'react';


//-----------------------------------------------------Bài tập 2---------------------------------------------------------//
function App() {
  const [userInputValue, setUserInputValue] = useState('')
  const [firstInputValue, setFirstInputValue] = useState('')
  const [secondInputValue, setSecondInputValue] = useState('')
  const [text, setText] = useState('')

  const handleUserInputChange = (e) => {
    setUserInputValue(e.target.value)
  }
  const handleFirstInputChange = (e) => {
    setFirstInputValue(e.target.value)
  }
  const handleSecondInputChange = (e) => {
    setSecondInputValue(e.target.value)
  }
  const handleSubmit = (e) => {
    e.preventDefault()
    if (userInputValue.trim()==='' || firstInputValue.trim()==='' || secondInputValue.trim()==='') {
      setText('Không được để trống!')
    } else {
      firstInputValue === secondInputValue ? setText(`Welcome, ${userInputValue}`) : setText('Bạn và tôi không giống nhau, con đường tôi bước đi, chỉ tôi mới hiểu được')
    }
    setFirstInputValue('')
    setSecondInputValue('')
  }

  return (
    <div className='App'>
      <h1>Đăng nhập</h1>
      <form onSubmit={handleSubmit}>
        <input type='text' placeholder='username' value={userInputValue} onChange={handleUserInputChange} pattern='{}'></input>
        <input type='text' placeholder='password' value={firstInputValue} onChange={handleFirstInputChange}></input>
        <input type='text' placeholder='confirm password' value={secondInputValue} onChange={handleSecondInputChange}></input>
        <button type='submit'>Submit</button>
      </form>
      <p>{text}</p>
    </div>
  );
}

export default App;
